//   // headerTintColor: Platform.OS === 'android' ? 'white' : Colors.primaryColor
// };

const styles = StyleSheet.create({
  screen: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  }
});

export default CategoriesScreen
